mapper.select:
		SELECT "userid", 
		extract(epoch from nextavailabledate) as times,
		"bloodgroup", 
		ST_Distance("lastlocation", st_setsrid(st_makepoint(#{longCoord},#{latCoord}),4326)) as distance 
		from userstatus
		WHERE bloodgroup IN   

        #{item}

		AND extract(epoch from nextavailabledate) < extract(epoch from #{currentDate} :: Date) AND not "userid" = #{userId}
		ORDER BY lastlocation <-> st_setsrid(st_makepoint(#{longCoord},#{latCoord}),4326)
		LIMIT #{kVal};

SELECT "userid", extract(epoch from nextavailabledate) as times, "bloodgroup", ST_Distance("lastlocation", st_setsrid(st_makepoint(#{longCoord},#{latCoord}),4326)) as distance from userstatus WHERE bloodgroup IN
#{item}
AND extract(epoch from nextavailabledate) < extract(epoch from #{currentDate} :: Date) AND not "userid" = #{userId} AND ST_DWithin("lastlocation", st_setsrid(st_makepoint(#{longCoord},#{latCoord}),4326), #{rangeVal}) ORDER BY lastlocation <-> st_setsrid(st_makepoint(#{longCoord},#{latCoord}),4326);
